mapper.insert:
INSERT INTO interfaceinfo
        (system_code, API_code, API_name, version, api_doc_uri,`status`,create_user,relation_system_code)
VALUES (#{systemCode}, #{apiCode}, #{apiName}, #{version}, #{apiDocUrl},#{status},#{createUser},#{relationSystemCode});

mapper.update:
UPDATE interfaceinfo
SET API_name=#{apiName}, version=#{version}, api_doc_uri=#{apiDocUrl},status=#{status},relation_system_code=#{relationSystemCode}
WHERE system_code=#{systemCode}
and API_code=#{apiCode};

mapper.select:
SELECT id,system_code as systemCode, API_code as apiCode, API_name as apiName, version, api_doc_uri as apiDocUrl,status,create_user, create_datetime,relation_system_code as relationSystemCode
FROM interfaceinfo
WHERE id=#{id}

SELECT id,system_code as systemCode, API_code as apiCode, API_name as apiName, version, api_doc_uri as apiDocUrl, status,create_user, create_datetime,relation_system_code as relationSystemCode
FROM interfaceinfo
WHERE system_code=#{param1}
and API_code=#{param2}
